function editEmailTemplate(e){showSpinner(),$.post({url:"/actions/admin_edit_email_template.php",data:{email_template_id:e},dataType:"json",success:e=>{$("#email_template > .row").html(e.template),$(".page-content").prepend(e.msg),initListenerEdit()}}).always(()=>hideSpinner())}function listEmailTemplate(){showSpinner(),$.post({url:"/actions/admin_list_email_template.php",dataType:"json",success:e=>{$("#email_template > .row").html(e.template),$(".page-content").prepend(e.msg),initListenerEdit()}}).always(()=>hideSpinner())}$(function(){$(".add_new_email_template_list").on("click",()=>{editEmailTemplate()})});var timeout,delay=500;function initListenerEdit(){$("#email_template_content").on("keyup",function(){var e=$(this);clearTimeout(timeout),timeout=setTimeout(()=>{$("#render").html(e.val())},delay)})}