INSERT INTO `{tbl_prefix}email` (`code`, `id_email_template`, `is_deletable`, `title`, `content`, `disabled`, `code_unique_for_enable`) VALUES
    ('share_video_template', 1, 0, '[{{website_title}}] - {{user_username}} wants to share a video with you', '<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"5\"><br>                  <tr><br>                    <td bgcolor=\"#53baff\" ><span class=\"title\">{{website_title}}</span>share video</td><br>                  </tr><br>                  <tr><br>                    <td height=\"20\" class=\"messege\">{{user_username}} wants to share Video With You<br>                      <div id=\"videoThumb\"><a href=\"{{url}}\"><img src=\"{{thumb_url}}\"><br><br>                    watch video</a></div></td><br>                  </tr><br>                  <tr><br>                    <td class=\"text\" ><span class=\"title2\">Video Description</span><br><br>                      <span class=\"text\">{{message_subject}}</span></td><br>                  </tr><br>                  <tr><br>                    <td><span class=\"title2\">Personal Message</span><br><br>                      <span class=\"text\">{{message_content}}<br>                      </span><br><br>                      <br><br>                <span class=\"text\">Thanks,</span><br> <br>                <span class=\"text\">{{user_username}}</span></td><br>                  </tr><br>                  <tr><br>                    <td bgcolor=\"#53baff\">copyrights {{time}} {{website_title}}</td><br>                  </tr><br>                </table>', 0, 'share_video_template'),
    ('photo_share_template', 1, 0, '[{{website_title}}] - {{user_username}} wants to share a photo with you', '<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"5\"><br>                  <tr><br>                    <td bgcolor=\"#53baff\" ><span class=\"title\">{{website_title}}</span>share video</td><br>                  </tr><br>                  <tr><br>                    <td height=\"20\" class=\"messege\">{{user_username}} wants to share Video With You<br>                      <div id=\"videoThumb\"><a href=\"{{url}}\"><img src=\"{{thumb_url}}\"><br><br>                    watch video</a></div></td><br>                  </tr><br>                  <tr><br>                    <td class=\"text\" ><span class=\"title2\">Video Description</span><br><br>                      <span class=\"text\">{{message_subject}}</span></td><br>                  </tr><br>                  <tr><br>                    <td><span class=\"title2\">Personal Message</span><br><br>                      <span class=\"text\">{{message_content}}<br>                      </span><br><br>                      <br><br>                <span class=\"text\">Thanks,</span><br> <br>                <span class=\"text\">{{user_username}}</span></td><br>                  </tr><br>                  <tr><br>                    <td bgcolor=\"#53baff\">copyrights {{time}} {{website_title}}</td><br>                  </tr><br>                </table>', 0, 'share_video_template'),
    ('collection_share_template', 1, 0, '[{{website_title}}] - {{user_username}} wants to share a collection with you', '<table width="100%" border="0" cellspacing="0" cellpadding="5">             <tr>                  <td bgcolor="#0099cc" ><span class="title">{{website_title}}</span></td>             </tr><tr>                  <td height="20" class="messege">{{user_username}} wants to share this collection with you.<br>                       <div id="videoThumb"><a class="text" href="{{url}}"><img src="{{thumb_url}}"><br>                  View Collection <small class=''text2''>({{total_items}} {{type}})</small></a></div></td>             </tr>             <tr>                  <td class="text" ><span class="title2">Collection Description</span><br>                       <span class="text">{{message_subject}}</span></td>             </tr>             <tr>                  <td><span class="title2">Personal Message</span><br>                       <span class="text">{{message_content}}                     </span><br>                       <br>                    <span class="text">Thanks,</span><br><span class="text">{{website_title}}</span></td>             </tr>             <tr>                  <td bgcolor="#0099cc">copyrights {{time}} {{website_title}}</td>             </tr>        </table>', 0, 'share_video_template'),
    ('email_verify_template', 1, 0, '[{{website_title}}]- Account activation email', 'Hello {{user_username}},<br>            Thank you for joining {{website_title}}, one last step is required in order to activate your account<br><br>            <a href=\'{{url}}activation.php?av_username={{user_username}}&avcode={{code}}\'>Click Here</a><br>            <br>            Email           : {{user_email}}<br>            Username        : {{user_username}}<br>            Activation code : {{code}}<br>            <br>            if above given is not working , please go here and activate it<br>            <a href=\'{{url}}activation.php\'>Activate</a>', 0, 'email_verify_template'),
    ('pm_email_message', 1, 0, '[{{website_title}}] - {{user_username}} has sent you a private message', '{{user_username}} has sent you a private message, <br><br>                {{message_subject}}<br>                \"{{message_content}}\"<br>                <br>                click here to view your inbox <a href=\"{{url}}\">Inbox</a><br>                <br>                {{website_title}}', 0, 'pm_email_message'),
    ('welcome_message_template', 1, 0, 'Welcome {{user_username}} to {{website_title}}', 'Hello {{user_username}},<br>            Thanks for joining at {{website_title}}!, you are now part of our community and we hope you will enjoy your stay<br>            <br>            All the best,<br>            {{website_title}}', 0, 'welcome_message_template'),
    ('password_reset_request', 1, 0, '[{{website_title}}] - Password reset confirmation', 'Dear {{user_username}}<br>            you have requested a password reset, please follow the link in order to reset your password<br>            <a href=\"{{url}}\">Reset my password</a><br>            <br>            -----------------------------------------<br>            IF YOU HAVE NOT REQUESTED A PASSWORD RESET - PLEASE IGNORE THIS MESSAGE<br>            -----------------------------------------<br>            Regards<br>            {{website_title}}', 0, 'password_reset_request'),
    ('password_reset_details', 1, 0, '[{{website_title}}] - Password reset details', 'Dear {{user_username}}<br>                your password has been reset<br>                your new password is : {{password}}<br>                <br>                <a href=\"{{url}}\">click here to login to website</a><br>                <br>                ---------------<br>                Regards<br>                {{website_title}}', 0, 'password_reset_details'),
    ('forgot_username_request', 1, 0, '[{{website_title}}] - your {{website_title}} username', 'Hello,<br>            your {{website_title}} username is : {{user_username}}<br>            <br>            --------------<br>            Regards<br>            {{website_title}}', 0, 'forgot_username_request'),
    ('friend_request_email', 1, 0, '[{{website_title}}] {{user_username}} add you as friend', 'Hi {{receiver_name}},<br>            {{sender_name}} added you as a friend on {{website_title}}. We need to confirm that you know {{sender_name}} in order for you to be friends on {{website_title}}.<br>            <br>            <a href=\"{{sender_link}}\">View profile of {{sender_name}}</a> <br>            <a href=\"{{request_link}}\">click here to respond to friendship request</a><br>            <br>            Thanks,<br>            {{website_title}} Team', 0, 'friend_request_email'),
    ('friend_confirmation_email', 1, 0, '[{{website_title}}] - {{sender_name}} has confirmed you as a friend', 'Hi {{receiver_name}},<br>            {{sender_name}} confirmed you as a friend on {{website_title}}.<br>            <br>            <a href=\"{{sender_link}}\">View {{sender_name}} profile</a><br>            <br>            Thanks,<br>            The {{website_title}} Team', 0, 'friend_confirmation_email'),
    ('contact_form', 1, 0, '[{{website_title}} - Contact] {{message_subject}} from {{user_username}}', 'Name : {{user_username}}<br>                Email : {{user_email}}<br>                Reason : {{message_subject}}<br>                <br>                Message:<br>                {{message_content}}<br>                <br>                <hr><br>                Ip : {{url}}<br>                date : {{time}}', 0, 'contact_form'),
    ('video_activation_email', 1, 0, '[{{website_title}}] - Your video has been activated', 'Hello {{user_username}},<br>                Your video has been reviewed and activated by one of our staff, thanks for uploading this video. You can view this video here.<br>                {{url}}<br>                <br>                Thanks<br>                {{website_title}} Team', 0, 'video_activation_email'),
    ('user_comment_email', 1, 0, '[{{website_title}}] {{user_username}} made comment on your {{message_subject}}', '{{user_username}} has commented on your {{message_subject}}<br>                \"{{message_content}}\"<br>                <br>                <a href=\"{{url}}\">{{url}}</a><br>                <br>                {{website_title}} team', 0, 'user_comment_email'),
    ('user_reply_email', 1, 0, '[{{website_title}}] {{user_username}} made reply on your comment', '{{user_username}} has replied on your comment<br>                \"{{message_content}}\"<br>                <br>                <a href=\"{{url}}\">{{url}}</a><br>                <br>                {{website_title}} team', 0, 'user_reply_email'),
    ('video_subscription_email', 1, 0, '{{sender_name}} has uploaded new video on {{website_title}}', 'Hello {{user_username}}<br><br>                You have been notified by {{website_title}} that {{sender_name}} has uploaded new video <br>                <br>                Video Title : {{message_subject}}<br>                Video Description : {{message_content}}<br>                <br>                <br>                <a href=\"{{url}}\"><br>                <img src=\"{{thumb_url}}\" border=\"0\" height=\"90\" width=\"120\"><br><br>                click here to watch this video</a><br>                <br>                <br>                You are notified because you are subscribed to {{sender_name}}, you can manage your subscriptions by going to your account and click on manage subscriptions.<br>                {{website_title}}', 0, 'video_subscription_email'),
    ('avcode_request_template', 1, 0, '[{{website_title}}] - {{user_username}} has sent you a private message', 'Hello {{user_username}},<br>Your Activation Code is : {{code}}<br><a   href=''{{url}}/activation.php?av_username={{user_username}}&avcode={{code}}''>Click Here</a> To goto Activation Page<br><br>Direct Activation<br>==========================================<br>Click Here or Copy & Paste the following link in your browser<br>{{url}}/activation.php?av_username={{user_username}}&avcode={{code}}<br><br>if above given links are not working, please go here and activate it<br><br>Email           : {{user_email}}<br>Username        : {{user_username}}<br>Activation code : {{code}}<br><br>if above given is not working , please go here and activate it<br><a  href=''{{url}}/activation.php''>{{url}}/activation.php</a><br><br>----------------<br>Regards<br>{{website_title}}', 0, 'video_subscription_email');
